import React, {Component, useImperativeHandle} from 'react';
import logo from './logo512.png';
import './App.css';
import Popup from './components/popup.js';

class App extends Component {
  constructor(props){
    super(props);
    this.state = { 
      showPopup: false,
      isLoggedin: false,
      passwordsMatch: null,
       username: '',
       password:  '',
       checkpassword: '',
       message: "Pass don't match"
      };


    }

    handleChange(event) {
      event.preventDefault()

      this.setState({ [event.target.name]: event.target.value });
    }

    comparePassword() { 
      if(this.state.password !== this.state.checkpassword){

        this.setState({passwordsMatch: false})
       alert(this.state.password)
         //alert(this.props.passwordsMatch)
           return false; // The form won't submit
       }

       else 
       this.setState({passwordsMatch: true})
       return  true; // The form will submit
    }



    togglePopup() {
     this.setState({
       showPopup: !this.state.showPopup
     });
   }



    render() {
      return (
        <div>

<header className="App-header">



         <button onClick={this.togglePopup.bind(this)}>Login/register</button>

         {this.state.showPopup ?
           <Popup
            text='Registration'
            closePopup={this.togglePopup.bind(this)}
            onSubmit={this.handleChange.bind(this)}
            value={this.state}

            handleSubmit={this.comparePassword.bind(this)}
            message={this.state.passwordsMatch === false && <div>Passwords don't match!</div>}

           />
           : null
         }

         <img src={logo} className="App-logo" alt="logo" />

         </header>
        </div>



      );
    }
  }





export default App;
