import React, {Component} from 'react';
import logo from './logo512.png';
import './App.css';
import Popup from './components/popup.js';

class App extends Component {
  constructor(props){
    super(props);
    this.state = { 
      showPopup: false,
      isLoggedin: false,
      passwordsMatch: false,
       username: "",
       password:  "",
       checkpassword: "",
       message: "Pass don't match"
      };


    this.handleChange = this.handleChange.bind(this)

    }
handleChange(event) {

  this.setState({
    message: event.target.value
  })
}

    togglePopup() {
     this.setState({
       showPopup: !this.state.showPopup
     });
   }

   handleSubmit = (event) => {
    if(this.state.password !== this.state.checkpassword){
       this.passwordsMatch = false;
        return false; // The form won't submit
    }

    else return this.passwordsMatch = true &&  true; // The form will submit
 }

    render() {
      return (
        <div>

<header className="App-header">



         <button onClick={this.togglePopup.bind(this)}>Login/register</button>

         {this.state.showPopup ?
           <Popup
            text='Registration'
            closePopup={this.togglePopup.bind(this)}


            submitRegistration={this.handleSubmit.bind(this)}
            message={this.passwordsMatch.bind(this)}

           />
           : null
         }

         <img src={logo} className="App-logo" alt="logo" />

         </header>
        </div>



      );
    }
  }





export default App;
