import React from 'react';  
import './style.css';  

class Popup extends React.Component {  

  handleChange(e) {
  e.preventDefault()
  const {name, value} = e.target
  this.setState({
    [name]: value
  })
  console.log(value)

      /*if(this.props.password !== this.props.checkpassword){
        this.setState({passwordsMatch: false})
          return false; // The form won't submit
      }
      
      else 
      this.setState({passwordsMatch: true})
      return  true; // The form will submit
   }*/

  }

  


  render() {  
return (  
<div className='popup'>  
<div className='popup\_inner'> 
<button onClick={this.props.closePopup}>X</button>  

<div style={{display: 'flex', justifyContent: 'center', border: '1px solid white'}}>

<div>
<h1>{this.props.text}</h1>  
<form onSubmit={this.handleChange.bind(this)}>
<input value={this.props.username}
 placeholder="Enter username" /><br />
 <input type="password" value={this.props.password} 
 placeholder="Enter password" /><br />
 <input type="password" value={this.props.checkpassword}
 placeholder="Enter password again" /><br />
<input type="submit" className="hidden" />

<h1>{this.props.message}</h1>
</form>

</div>
</div>
</div>  
</div>  
);  
}  
}  

export default Popup;
