import React from 'react';  
import './style.css';  

class Popup extends React.Component {  

  handleChange(e) {
  e.preventDefault()
 
  

      if(this.refs.password !== this.refs.checkpassword){
        this.setState({passwordsMatch: false})
          return false; // The form won't submit
      }
      
      else 
      this.setState({passwordsMatch: true})
      return  true; // The form will submit
   }

  

  


  render() {  
return (  
<div className='popup'>  
<div className='popup\_inner'> 
<button onClick={this.props.closePopup}>X</button>  

<div style={{display: 'flex', justifyContent: 'center', border: '1px solid white'}}>

<div>
<h1>{this.props.text}</h1>  
<form onSubmit={this.handleChange.bind(this)}>
<input 
 placeholder="Enter username" type="text" ref="username" /><br />
 <input type="password" 
 value={this.props.password} 
 placeholder="Enter password" ref="password" /><br />
 <input type="password" 
 value={this.props.checkpassword}
 placeholder="Enter password again" ref="checkpassword" /><br />
<input type="submit" 
className="hidden" />

<h1>{this.props.message}</h1>
</form>

</div>
</div>
</div>  
</div>  
);  
}  
}  

export default Popup;
