import React from 'react';  
import './style.css';  

class Popup extends React.Component {  
  render() {  
return (  
<div className='popup'>  
<div className='popup\_inner'>  
<button onClick={this.props.closePopup}>X</button>  

<div style={{display: 'flex', justifyContent: 'center'}}>
<div>
<h1>{this.props.text}</h1>  
<form>
<input value={this.props.username} onChange={this.handleChange}
 placeholder="Enter username" /><br />
 <input type="password" value={this.props.password} onChange={this.handleChange}
 placeholder="Enter password" /><br />
 <input type="password" value={this.props.checkpassword} onChange={this.handleChange}
 placeholder="Enter password again" /><br />
<button onClick={this.props.submitRegistration}>Submit</button>
<h1>{this.props.message}</h1>
</form>

</div>
</div>
</div>  
</div>  
);  
}  
}  

export default Popup;
